import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [quote, setQuote] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showLogin, setShowLogin] = useState(true);
  const [token, setToken] = useState(localStorage.getItem('token') || '');

  useEffect(() => {
    if (token) {
      setIsLoggedIn(true);
    }
  }, [token]);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('username', username);
      formData.append('password', password);

      const response = await fetch('http://localhost:8000/token', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (response.ok) {
        setToken(data.access_token);
        localStorage.setItem('token', data.access_token);
        setIsLoggedIn(true);
        setShowLogin(false);
        setError('');
      } else {
        setError('Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. ÏïÑÏù¥ÎîîÏôÄ ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('ÏÑúÎ≤ÑÏôÄÏùò Ïó∞Í≤∞Ïóê Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: `username=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}`,
      });

      if (response.ok) {
        setError('ÌöåÏõêÍ∞ÄÏûÖÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§. Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
        setShowLogin(true);
      } else {
        const data = await response.json();
        if (typeof data.detail === 'string') {
          setError(data.detail);
        } else if (Array.isArray(data.detail)) {
          setError(data.detail.map(err => err.msg).join(', '));
        } else {
          setError('ÌöåÏõêÍ∞ÄÏûÖÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
        }
      }
    } catch (error) {
      console.error('Error:', error);
      setError('ÏÑúÎ≤ÑÏôÄÏùò Ïó∞Í≤∞Ïóê Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken('');
    setIsLoggedIn(false);
    setShowLogin(true);
    setQuote('');
  };

  const getNewQuote = async () => {
    setIsLoading(true);
    setError('');
    try {
      const response = await fetch('http://localhost:8000/quote', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      const data = await response.json();

      if (data.error) {
        setError(data.error);
      } else {
        setQuote(data.quote);
      }
    } catch (error) {
      console.error('Error:', error);
      setError('ÏÑúÎ≤ÑÏôÄÏùò Ïó∞Í≤∞Ïóê Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
    }
    setIsLoading(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Ïò§ÎäòÏùò ÏïÑÎ¨¥Îßê Î™ÖÏñ∏</h1>
        <h2>GPTÍ∞Ä Ï†ÑÌï¥Ï£ºÎäî, Ï†ÑÌòÄ Ïã§Ïö©Ï†ÅÏù¥ÏßÄ ÏïäÏùÄ Ïù∏ÏÉùÏùò ÏßÑÎ¶¨</h2>

        {!isLoggedIn ? (
          <div className="auth-container">
            {showLogin ? (
              <form onSubmit={handleLogin} className="auth-form">
                <h3>Î°úÍ∑∏Ïù∏</h3>
                <input
                  type="text"
                  placeholder="ÏïÑÏù¥Îîî"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
                <input
                  type="password"
                  placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                <button type="submit">Î°úÍ∑∏Ïù∏</button>
                <button type="button" onClick={() => setShowLogin(false)}>
                  ÌöåÏõêÍ∞ÄÏûÖ
                </button>
              </form>
            ) : (
              <form onSubmit={handleRegister} className="auth-form">
                <h3>ÌöåÏõêÍ∞ÄÏûÖ</h3>
                <input
                  type="text"
                  placeholder="ÏïÑÏù¥Îîî"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
                <input
                  type="password"
                  placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                <button type="submit">ÌöåÏõêÍ∞ÄÏûÖ</button>
                <button type="button" onClick={() => setShowLogin(true)}>
                  Î°úÍ∑∏Ïù∏ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
                </button>
              </form>
            )}
          </div>
        ) : (
          <>
            <div className="user-info">
              <span>ÌôòÏòÅÌï©ÎãàÎã§, {username}Îãò!</span>
              <button onClick={handleLogout} className="logout-button">
                Î°úÍ∑∏ÏïÑÏõÉ
              </button>
            </div>
            <button 
              onClick={getNewQuote} 
              disabled={isLoading}
              className="quote-button"
            >
              {isLoading ? 'ÏÉùÍ∞Å Ï§ë...' : 'üëÅÔ∏è‚Äçüó®Ô∏è Î™ÖÏñ∏ Î≥¥Í∏∞'}
            </button>
          </>
        )}

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        {quote && !error && (
          <div className="quote-box">
            <p className="quote-text">{quote}</p>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
